package practice_java_home;
import java.util.*;
public class squares {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		int n = sc.nextInt();
		for(int i=0; i<=n ; i++) {
			System.out.print(","+i*i+" ");
		}
	}

}
